package pri;

public class QuotientAndReminder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int d1 = 30;
		int divisor =4;
		int quotient = d1 / divisor;
		
		int reminder = d1 % divisor;
		System.out.println(quotient);
		System.out.println(reminder);
		
		
		

	}

}
